/* ============================================================
   FRONTEND MAIN STYLES — ATHI ESTATE SYSTEM
   Unified, contrast-optimized, and accessibility-compliant
   ============================================================ */

/* ------------------------------
   GOOGLE FONT
------------------------------ */
@import url("https://fonts.googleapis.com/css2?family=Inter:wght@300;400;600&display=swap");

/* ------------------------------
   COLOR VARIABLES
------------------------------ */
:root {
  --primary: #1e3a8a;
  --primary-light: #3b82f6;
  --secondary: #0f172a;
  --neutral-light: #f8fafc;
  --accent: #facc15;
  --danger: #dc2626;

  /* ✅ Improved accessibility contrast */
  --text-dark: #1e293b;
  --text-light: #f9fafb;
  --text-muted: #cbd5e1;
}

/* ------------------------------
   GLOBAL RESET & TYPOGRAPHY
------------------------------ */
* {
  font-family: "Inter", sans-serif !important;
  font-weight: 400;
  letter-spacing: 0.2px;
  box-sizing: border-box;
  color: inherit;
  margin: 0;
  padding: 0;
}

body {
  background-color: var(--secondary);
  color: var(--text-light);
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  overflow-x: hidden;
  line-height: 1.6;
}

/* ------------------------------
   HEADINGS & TEXT
------------------------------ */
h1,
h2,
h3,
h4,
h5,
h6 {
  font-weight: 600;
  color: #60a5fa;
  margin-bottom: 0.5rem;
}

p,
label,
a {
  color: var(--text-light);
  font-size: 0.95rem;
}

/* ------------------------------
   NAVBAR
------------------------------ */
header {
  background: rgba(15, 23, 42, 0.85);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  height: 60px;
  padding: 0.5rem 1rem;
  flex: 0 0 auto;
  z-index: 10;
}

nav ul {
  display: flex;
  gap: 1rem;
  list-style: none;
}

.nav-link {
  color: var(--text-light);
  opacity: 0.9;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease, opacity 0.3s ease;
}

.nav-link:hover,
.nav-link.active {
  color: #93c5fd;
  opacity: 1;
}

/* ------------------------------
   MAIN LAYOUT & BACKGROUND VIDEO
------------------------------ */
main {
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2rem;
  position: relative;
  z-index: 10;
}

video {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: -2;
  pointer-events: none;
}

.overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.6);
  z-index: -1;
  pointer-events: none;
}

/* ------------------------------
   FORM ELEMENTS
------------------------------ */
input,
textarea,
select {
  background: rgba(255, 255, 255, 0.15);
  color: var(--text-light);
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: 0.5rem;
  padding: 0.6rem 0.75rem;
  width: 100%;
  outline: none;
  transition: all 0.3s ease;
}

input::placeholder {
  color: #e2e8f0;
}

input:focus,
textarea:focus,
select:focus {
  border-color: #60a5fa;
  box-shadow: 0 0 6px rgba(96, 165, 250, 0.6);
}

/* ------------------------------
   BUTTONS
------------------------------ */
button,
.btn,
.login-btn,
.register-btn {
  background-color: #2563eb;
  color: #ffffff;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  padding: 0.6rem 1rem;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.2s ease;
}

button:hover,
.btn:hover {
  background-color: #1e40af;
  transform: translateY(-2px);
}

button:disabled,
.btn:disabled {
  background-color: #94a3b8;
  cursor: not-allowed;
}

/* ------------------------------
   GLASS CARD (darker for contrast)
------------------------------ */
.glass-card {
  background: rgba(15, 23, 42, 0.85);
  color: var(--text-light);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 1rem;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.4);
  padding: 1.5rem;
  width: 100%;
  max-width: 380px;
}

/* ------------------------------
   LOGIN / REGISTER CONTAINERS
------------------------------ */
.login-container {
  width: 100%;
  max-width: 380px;
  background: rgba(15, 23, 42, 0.85);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 16px;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.4);
  padding: 2rem;
  text-align: center;
}

.login-container h2 {
  color: var(--text-light);
  margin-bottom: 1.5rem;
}

/* ------------------------------
   FOOTER
------------------------------ */
footer {
  background-color: var(--primary);
  color: var(--text-light);
  text-align: center;
  padding: 0.5rem;
  font-size: 0.85rem;
  width: 100%;
}

/* ------------------------------
   RESPONSIVE DESIGN
------------------------------ */
@media (max-width: 640px) {
  header {
    flex-direction: column;
    text-align: center;
  }

  nav ul {
    flex-direction: column;
    gap: 10px;
  }

  .login-container,
  .glass-card {
    width: 90%;
  }

  main {
    padding: 1rem;
  }
}
/* Profile dropdown styling */
#profileDropdown {
  right: 0;
  top: 100%;
  background-color: #1f2937; /* gray-800 */
  color: white;
  border-radius: 0.5rem;
  min-width: 160px;
  box-shadow: 0 4px 10px rgba(0,0,0,0.2);
  z-index: 50;
}

#profileDropdown a,
#profileDropdown button {
  display: block;
  width: 100%;
  text-align: left;
  padding: 0.5rem 1rem;
}

#profileDropdown a:hover,
#profileDropdown button:hover {
  background-color: #374151; /* gray-700 */
}
